drop procedure if exists tmp_pro_insert_emp_attendance;
CREATE PROCEDURE tmp_pro_insert_emp_attendance(
	in p_customer_id VARCHAR(32), in p_user_name_ch VARCHAR(32),
	in p_opt_day FLOAT(4,2)
)
BEGIN

	set @session.empId = (SELECT emp_id FROM dim_user t WHERE t.user_name_ch = p_user_name_ch);

	-- emp_attendance
	DELETE FROM emp_attendance WHERE emp_id = (SELECT @session.empId) and `year_month` = 201811;


	-- emp_attendance_day
	DELETE FROM emp_attendance_day WHERE emp_id = (SELECT @session.empId) and `year_months` = 201811;


	-- emp_other_day
	DELETE FROM emp_other_day WHERE `year_months` = 201811;

	-- emp_vacation
	SET @session.annual = (SELECT annual FROM emp_vacation WHERE emp_id = (SELECT @session.empId));
	UPDATE emp_vacation SET annual = (SELECT @session.annual) WHERE emp_id = (SELECT @session.empId);
	UPDATE emp_vacation SET annual = (SELECT @session.annual )+ p_opt_day WHERE emp_id = (SELECT @session.empId);

	-- emp_attendance_month
  CALL pro_fetch_emp_attendance_month ( "1", "SYSTEM", (SELECT @session.empId), 201811);


	COMMIT;
END;


-- ----------------------------------------------------
-- 11月份考勤中对应的1.5天，11月5日1天 11月16日0.5天
-- ----------------------------------------------------
call tmp_pro_insert_emp_attendance('1','马龙', -1.5);